import React, { useState, useEffect } from 'react';
import Dialog from "@material-ui/core/Dialog";
import makeStyles from "@material-ui/core/styles/makeStyles";
import DialogContent from '@material-ui/core/DialogContent';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import Grid from "@material-ui/core/Grid";
import Slide from '@material-ui/core/Slide';

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="down" ref={ref} {...props} />;
});

const BANNER_LOCALE_KEY = 'testPromo';

const useStyle = makeStyles(() => ({
  root: {
    maxWidth: '600px',
    borderRadius: '10px',
    margin: '0 auto'
  },
  content: {
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundImage: 'url(\'data:image/jpeg;base64,\')',
  },
  counter: {
    position: 'absolute',
    top: '20px',
    right: '20px',
    backgroundColor: 'rgba(0, 0, 0, .3)',
    color: 'white',
    padding: '5px 10px',
    borderRadius: '100px'
  },
  footer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    color: 'white',
    padding: '7px 0',
    backgroundColor: 'rgba(0, 0, 0, .1)',
  }
}));

export const BannerModule = () => {
  const classes = useStyle();
  const [count, setCount] = useState(3);
  const [isOpen, setIsOpen] = useState(false);
  const [intervalId, setIntervalId] = useState(null);

  const onClose = () => {
    setIsOpen(false);
    clearInterval(intervalId);
    setCount(0);
  }

  useEffect(() => {
    const localeBannerValue = window.localStorage.getItem(BANNER_LOCALE_KEY);

    if(localeBannerValue !== '1') {
      window.localStorage.setItem(BANNER_LOCALE_KEY, '1');

      setIsOpen(true);

      setIntervalId(setInterval(() => {
        if(count > 0) setCount((count) => count - 1);
      }, 1000));

      return () => {
        setIsOpen(false);
        clearInterval(intervalId);
      };
    }
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    if(count === 0) {
      onClose()
    }
    // eslint-disable-next-line
  }, [count]);

  const BackdropProps = {
    style: {
      backgroundColor: 'rgba(0, 0, 0, .7)',
    }
  };

  return (
    <Dialog TransitionComponent={Transition} BackdropProps={BackdropProps} fullScreen className={classes.root} fullWidth={true} open={isOpen} onClose={onClose}>
      <DialogContent className={classes.content}>
        <div className={classes.counter} onClick={onClose}>
          Пропустить {count}
        </div>
        <Grid container justify='center' className={classes.footer} onClick={onClose}>
          <HighlightOffIcon style={{ fontSize: 40, color: 'white' }} />
        </Grid>
      </DialogContent>
    </Dialog>
  );
};
